import React, { useState } from "react";
import axios from "axios";
import "./ServerForm.css";

function ServerForm() {
  const [name, setName] = useState("");
  const [language, setLanguage] = useState("");
  const [framework, setFramework] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post("http://localhost:8080/servers", { name, language, framework })
      .then((res) => {
        console.log(res.data);
        setName("");
        setLanguage("");
        setFramework("");
        setError("");
      })
      .catch((err) => {
        console.log(err);
        setError("Error creating server.");
      });
  };

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleLanguageChange = (e) => {
    setLanguage(e.target.value);
  };

  const handleFrameworkChange = (e) => {
    setFramework(e.target.value);
  };

  return (
    <div className="server-form-container">
      <h2>Create Server</h2>
      <form className="server-form" onSubmit={handleSubmit}>
        <div className="server-form-input-group">
          <label className="server-form-label" htmlFor="name">Name:</label>
          <input className="server-form-input" type="text" id="name" value={name} onChange={handleNameChange} required />
        </div>
        <div className="server-form-input-group">
          <label className="server-form-label" htmlFor="language">Language:</label>
          <input className="server-form-input" type="text" id="language" value={language} onChange={handleLanguageChange} required />
        </div>
        <div className="server-form-input-group">
          <label className="server-form-label" htmlFor="framework">Framework:</label>
          <input className="server-form-input" type="text" id="framework" value={framework} onChange={handleFrameworkChange} required />
        </div>

    {error && <p className="server-form-error">{error}</p>}
    <button className="server-form-submit-btn" type="submit">Create</button>
  </form>
</div>
);
}

export default ServerForm;
