import React, { useState } from "react";
import axios from "axios";
import "./SearchById.css";

function SearchById({ onSubmit }) {
  const [id, setId] = useState("");
  const [server, setServer] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .get(`http://localhost:8080/servers/${id}`)
      .then((res) => {
        setServer(res.data);
        onSubmit(res.data);
      })
      .catch((err) => {
        console.log(err);
        setServer(null);
        onSubmit(null);
      });
  };

  const handleChange = (e) => {
    setId(e.target.value);
  };

  return (
    <div className="search-by-id">
      <h2>Search by ID</h2>
      <form onSubmit={handleSubmit}>
        <div className="search-by-id__form-group">
          <label className="search-by-id__label">
            Server ID:
            <input
              className="search-by-id__input"
              type="text"
              value={id}
              onChange={handleChange}
            />
          </label>
          <button className="search-by-id__submit" type="submit">
            Search
          </button>
        </div>
      </form>
      {server ? (
        <div className="search-by-id__result">
          <h3>Server details:</h3>
          <p>ID: {server.id}</p>
          <p>Name: {server.name}</p>
          <p>Language: {server.language}</p>
          <p>Framework: {server.framework}</p>
        </div>
      ) : (
        <div className="search-by-id__result">
          <p>No Server with this ID</p>
        </div>
      )}
    </div>
  );
}

export default SearchById;
