import React, { useState } from "react";
import axios from "axios";

function ServerForm() {
  const [server, setServer] = useState({
    id: "",
    name: "",
    language: "",
    framework: "",
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post("http://localhost:8080/servers", server)
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setServer((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
      Create a Server Object <br></br>
        Server ID:
        <input type="text" name="id" value={server.id} onChange={handleChange} />
      </label>
      <label>
        Server Name:
        <input type="text" name="name" value={server.name} onChange={handleChange} />
      </label>
      <label>
        Language:
        <input type="text" name="language" value={server.language} onChange={handleChange} />
      </label>
      <label>
        Framework:
        <input type="text" name="framework" value={server.framework} onChange={handleChange} />
      </label>
      <button type="submit">Create</button>
    </form>
  );
}

export default ServerForm;
