import React, { useState } from "react";
import axios from "axios";
import "./ServerForm.css";

function ServerForm({ onServerCreated }) {
  const [server, setServer] = useState({
    id: "",
    name: "",
    language: "",
    framework: "",
  });
  const [message, setMessage] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:8080/servers", server)
      .then((res) => {
        onServerCreated(res.data);
        setMessage("Server created successfully!");
        setServer({
          id: "",
          name: "",
          language: "",
          framework: "",
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setServer((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <form className="server-form" onSubmit={handleSubmit}>
      <h2 className="server-form__title">Create a Server Object</h2>
      <div className="server-form__group">
        <label className="server-form__label">Server ID:</label>
        <input
          className="server-form__input"
          type="text"
          name="id"
          value={server.id}
          onChange={handleChange}
        />
      </div>
      <div className="server-form__group">
        <label className="server-form__label">Server Name:</label>
        <input
          className="server-form__input"
          type="text"
          name="name"
          value={server.name}
          onChange={handleChange}
        />
      </div>
      <div className="server-form__group">
        <label className="server-form__label">Language:</label>
        <input
          className="server-form__input"
          type="text"
          name="language"
          value={server.language}
          onChange={handleChange}
        />
      </div>
      <div className="server-form__group">
        <label className="server-form__label">Framework:</label>
        <input
          className="server-form__input"
          type="text"
          name="framework"
          value={server.framework}
          onChange={handleChange}
        />
      </div>
      <button className="server-form__submit" type="submit">
        Create
      </button>
      {message && <p className="server-form__message">{message}</p>}
    </form>
  );
}

export default ServerForm;
